mapper.update:
UPDATE task
create_time= #{task.createTime,jdbcType=TIMESTAMP}, update_time= #{task.updateTime,jdbcType=TIMESTAMP}, date= #{task.date,jdbcType=TIMESTAMP}, status= #{task.status,jdbcType=VARCHAR}, type= #{task.type,jdbcType=VARCHAR}, checkpoint_date= #{task.checkpointDate,jdbcType=TIMESTAMP}, note= #{task.note,jdbcType=VARCHAR}
WHERE id = #{task.id,jdbcType=BIGINT}
